import React from "react";
import "./activityCard.css";

export const ActivityCard = (props) => {
  const { colour, image, label, timePeriod, duration, previousDuration } =
    props;

  const backgroundImages = {
    work: "./images/icon-work.png",
    play: "./images/icon-self-play.png",
    study: "./images/icon-study.png",
    exercise: "./images/icon-exercise.png",
    social: "./images/icon-social.png",
    "self-care": "./images/icon-self-care.png",
  };

  console.log(label);
  console.log(backgroundImages.label);
  const style = {
    backgroundImage: `url(${backgroundImages[label]})`,
    backgroundColor: "white",
  };

  return (
    <div className="activity-card">
      <div className="card-header" style={style}></div>
      <div className="card-body">
        <div className="card-body-top">
          <p className="activity">{label}</p>
          <p className="ellipsis">•••</p>
        </div>
        <p className="duration">32hrs</p>
        <p className="previousDuration">
          Last {timePeriod} - {previousDuration}
        </p>
      </div>
    </div>
  );
};
